import React, { Component } from 'react';
import Navigationbar from '../navigation';
// import userProfile from './profile';
import '@fortawesome/fontawesome-free/css/all.min.css';
// import backgroundImage from '../images/menuCard.jpg';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import { Form, Button, Card, CardGroup} from 'react-bootstrap';
import axios from 'axios';
// import { getMainCourse } from './getMaincourse';

export class eventDetails extends Component {
    constructor(props) {
        super(props);
        this.state = {
            eventList: []
        };
    }

    componentDidMount() {
        axios.get(`http://localhost:3001/yelp/viewEvents`)
        .then(res => {
            //console.log(res.data)
            this.setState({ eventList: res.data });
            //console.log(this.state.appetizerList);
        });
    }

    render () {
        return (
            <React.Fragment>
                <Navigationbar/>
                <div class="container">
                    <center>
                    <h1 style={{margin: "10px"}}> Name of the event </h1>
                    <h6> <i class="fas fa-calendar-day"></i> 10/3/2020</h6>
                    <h6> <i class="fas fa-hourglass"></i> Time </h6>
                    <h6> <i class="fas fa-map-pin"></i> Location </h6>
                    <p> Description of the evenet that we nneed to knpw abouy and hwo ut foes</p>
                    <p> #yoyohoneysingh</p>
                    <Button> Register </Button>
                    </center>
                </div>
            </React.Fragment>

        )
    }

}
export default eventDetails;
