import React, { Component } from 'react';
import Navigationbar from '../../navigation';
// import userProfile from './profile';
import '@fortawesome/fontawesome-free/css/all.min.css';
// import backgroundImage from '../images/menuCard.jpg';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import { Form, Button, Card, CardGroup} from 'react-bootstrap';
import axios from 'axios';

class Appetizer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            appetizer: []
        };
    }

    componentDidMount() {
        axios.get(`http://localhost:3001/yelp/viewMenu/appetizer/${localStorage.getItem("rest_id")}`)
        .then(res => {
            const appetizer = res.data;
            this.setState({ appetizer: appetizer });   
        })
    }

      render () {
          if (this.state && this.state.appetizer && this.state.appetizer.length>0) {
              for (var i=0; i<this.state.appetizer.length; i++) {
                  return (
                    <div >
                    <nav class="navbar" style={{backgroundColor: "red"}}>
                            <h1 style={{marginLeft:"600px", color:"white"}}> {this.state.appetizer.category} </h1>
                    </nav>
                      <Card> </Card>
                    </div>
                  )
              }
          }
      }

    }
export default Appetizer;
