import React, { Component } from 'react';
import Navigationbar from '../navigation';
import userProfile from './profile';
import '@fortawesome/fontawesome-free/css/all.min.css';
import PropTypes from 'prop-types';
import profilepic from './../images/download.png'
import { getUser, updateUser } from '../../actions/userProfileAction';
import { connect } from 'react-redux';
// import { customerSignup } from '../../actions/signupActions'
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import { UserProfileNavBar, UserProfileJumbo, NavList, Form, Button, MDBInput} from 'react-bootstrap';
import YelpImage from './../images/yelp_logo.jpg'
import axios from 'axios';

class BasicDetails extends Component {
  constructor(props) {
    super(props);
    this.state= {
      profile: {}
    };
    this.onChange = this.onChange.bind(this);
    this.onUpload = this.onUpload.bind(this);
  }

  componentWillMount() {
    axios.get(`/yelp/userProfile/${localStorage.getItem("user_id")}`)
    .then(response => 
        {console.log(response.data)
            this.setState({
                profile: (response.data) 
            }); 
        })
}

onChange = (e) => {
  this.setState({
      [e.target.name]: e.target.value
  })
}

updateProfile = (e) => {
  //prevent page from refresh
  e.preventDefault();
  let details = this.state.profile
        const data = {
            first_name: this.state.first_name || details.first_name,
            last_name: this.state.last_name || details.last_name,
            contactNo: this.state.contactNo || details.contactNo,
            email: this.state.email || details.email,
            gender: this.state.gender || details.gender,
            dateofbirthday: this.state.dateofbirthday || details.dateofbirthday,
            nickname: this.state.nickname || details.nickname,
            address: this.state.address || details.address,
            thingsilove: this.state.thingsilove || details.thingsilove,
            notyelping: this.state.notyelping || details.notyelping,
            headline: this.state.headline || details.headline,
            website: this.state.website || details.website,
            yelpingsince: this.state.yelpingsince || details.yelpingsince
        }
  // // data = Object.assign({}, this.state);
  // console.log(data);
  this.props.updateUser(data);
};
    render() {
      const error = {
        message: null
      }
      const success = {
        message: null
      }

      details = this.state.profile;
                // console.log(this.props.description)
      if(this.props.description == 'USER_UPDATED'){
        success.message = 'Successfully updated the new user.'
      }
      return (
        <React.Fragment>
          <Navigationbar />
        <div class="row">
          <div class='col-xs-3' style={{ marginLeft: "20px"}}>
                    <p style={{ marginLeft: "10px", marginTop: "10px", fontWeight: "bold"}}> Rakshitha's Profile</p>
                    <hr />
                    <ul class="list-unstyled">
                        <li style={{margin: "10px"}}>
                            <a href="#">
                                Profile Overview
                            </a>
                        </li>
                        <hr/>
                        <li style={{margin: "10px"}}>
                            <a href="#">
                                Messages
                            </a>
                        </li>
                        <hr/>
                        <li style={{margin: "10px"}}>
                            <a href="#">
                                Settings
                            </a>
                        </li>
                    </ul>
            </div>
          <div class='container'>
            <div class='row'>
              <div class='col-md-6' style={{ marginBottom: '5%' }}>
                <h3 style={{ margin: "15px, 0px", color: 'red', float: 'left' }}>Profile</h3>
                <br />
                <hr class='mb-3'></hr>
                <Form.Group controlId='firstName'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong >First Name</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    This field is required.
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='lastName'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Last Name</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    This field is required.
                  </Form.Text>
                  <Form.Control  onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='gender'>
                    <Form.Label style={{margin: "0px", padding: "0px"}}>
                        <strong>Gender</strong>
                    </Form.Label>
                    <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    This field is required.
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' placeholder="Male/Female" />
                    {/* <label> <input type="radio" value="option1" /> Female </label> <br />
                    <label> <input type="radio" value="option1" /> Male </label> */}
                </Form.Group>
                <Form.Group controlId='contact'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Contact number</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    This field is required.
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='dob'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Date Of Birth</strong>
                  </Form.Label>
                  <Form.Control
                    type='text'
                    name='dob'
                    placeholder='23 September 2001'
                    onChange={this.onChange}
                  />
                </Form.Group>
                <Form.Group controlId='address'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Address</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    This field is required.
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='nickName'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Nick Name</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    The Boss, Calamity Jane, The Prolific Reviewer
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>

                <Form.Group controlId='yelpingsince'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Yelping since</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    May 2017
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='yelpingSince'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Things I love...</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                  Comma separated phrases (e.g. sushi, Radiohead, puppies)
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='notYelping'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>When I am not yelping...</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                  Im missing out, Im working at the art gallery, Im probably at the movies
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='headline'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong>Your Headline</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                    Taco Tuesday Aficionado, The Globetrotting Reviewer
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Form.Group controlId='blob'>
                  <Form.Label style={{margin: "0px", padding: "0px"}}>
                    <strong> My blob or Website</strong>
                  </Form.Label>
                  <Form.Text style={{margin: "0px", padding: "0px"}} className='text-muted'>
                  www.example.com/myawesomeblog
                  </Form.Text>
                  <Form.Control onChange={this.onChange} type='text' />
                </Form.Group>
                <Button href='/userProfile' onClick = {this.onUpdate} variant='danger' type='submit'>
                  Save Changes
                </Button>
                <a href='/userProfile' style={{ marginLeft: '15px' }}>
                  Cancel
                </a>
              </div>
            </div>
          </div>
          </div>
        </React.Fragment>
      );
    }
  }


  BasicDetails.propTypes = {
    updateUser: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired
}

const mapStateToProps = state => { 
    return ({
    user: state.userProfile.user
})};

export default connect(mapStateToProps, { updateUser })(BasicDetails);

