import React, { Component } from 'react';
import Navigationbar from '../navigation';
// import userProfile from './profile';
import '@fortawesome/fontawesome-free/css/all.min.css';
// import backgroundImage from '../images/menuCard.jpg';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import { Form, Button, Card, CardGroup} from 'react-bootstrap';
import axios from 'axios';
import backendServer from "../../backendServer";

export class getDish extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menuList: []
        };
    }

    componentDidMount() {
        axios.get(`${backendServer}/yelp/viewMenu/${localStorage.getItem("rest_id")}`)
        .then(res => {
            console.log(res.data)
            this.setState({ menuList: res.data });
            //console.log(this.state.appetizerList);
        });
    }

    render () {
        // console.log(this.state.saladList);
        let renderMenu = this.state.menuList.map(menu => {
            return (
                <div class="container">
                    <Card style={{marginLeft: "25px"}}>
                        <Card.Title style={{fontSize: "25px", fontWeight: "bold"}}>{menu.dish_name} </Card.Title>
                        <Card.Text> <span style={{fontWeight: "bold"}}>Category:</span> {menu.category} </Card.Text>
                        <Card.Text><span style={{fontWeight: "bold"}}>Ingredients:</span> {menu.ingredients}</Card.Text>
                        <Card.Text> <span style={{fontWeight: "bold"}}>Description:</span> {menu.description}</Card.Text>
                        <Card.Text> ${menu.price}</Card.Text>
                        <Button>Order now </Button>
                        </Card>
                    <br/>
                    <br/>
                </div>
            )
        })
        return (
            <React.Fragment>
            <Navigationbar/>
            <div class="container">
                <center>
                <h1 style={{margin: "10px"}}> Full menu </h1>
                </center>
                    {renderMenu}
            </div>
        </React.Fragment>
        )
    }

}
export default getDish;
