import React, { Component } from 'react';
import Navigationbar from '../navigation';
// import userProfile from './profile';
import '@fortawesome/fontawesome-free/css/all.min.css';
// import backgroundImage from '../images/menuCard.jpg';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import { Form, Button, Card, CardGroup} from 'react-bootstrap';
import axios from 'axios';
import backendServer from '../../backendServer';
// import { getMainCourse } from './getMaincourse';

export class userOrders extends Component {
    constructor(props) {
        super(props);
        this.state = {
            yourOrders: [],
            filterList: [],
            filterSelected: 'False' 

        };
    }

    componentDidMount() {
        axios.get(`${backendServer}/yelp/order/${localStorage.getItem("user_id")}`)
        .then(res => {
            //console.log(res.data)
            this.setState({ yourOrders: res.data });
            //console.log(this.state.appetizerList);
        });

        axios.get(`${backendServer}/yelp/order/${localStorage.getItem("user_id")}/${this.state.order_status}`)
        .then(res => {
            this.setState({ filterList: res.data });
        });
    }

    handleFilter = (e) => {

        this.setState({
            filterSelected: 'True'
        })

        this.setState({
            order_status: e.target.id
        })  
    }



    render () {
        console.log(this.state.yourOrders);
        let renderOrders;
        let renderFilterOrders;

        if(this.state.filterSelected === 'True'){
            renderFilterOrders = this.state.filterList.map(order => {
                return (
                    <div>
                        <Card style={{border: "none"}}>
                            <Card.Title style={{marginLeft:"10px", fontSize: "25px"}}>{order.dish_name} </Card.Title>
                            <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}>Restuarant:</span> {order.rest_name}</Card.Text>
                            <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}>Order type:</span> {order.order_type}</Card.Text>
                            <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}> Order date </span> {order.date}</Card.Text>
                            <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}> Order time </span> {order.time}</Card.Text>
                            <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}>Order status:</span> {order.order_status} </Card.Text>
                        </Card>
                        <hr />
                        <br/>
                        <br/>
                    </div>
                )
            }) 
        } else {

        renderOrders = this.state.yourOrders.map(order => {
            return (
                <div>
                    <Card style={{border: "none"}}>
                        <Card.Title style={{marginLeft:"10px", fontSize: "25px"}}>{order.dish_name} </Card.Title>
                        <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}>Restuarant:</span> {order.rest_name}</Card.Text>
                        <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}>Order type:</span> {order.order_type}</Card.Text>
                        <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}> Order date </span> {order.date}</Card.Text>
                        <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}> Order time </span> {order.time}</Card.Text>
                        <Card.Text> <span style={{fontWeight: "bold", marginLeft:"10px"}}>Order status:</span> {order.order_status} </Card.Text>
                    </Card>
                    <hr />
                    <br/>
                    <br/>
                </div>
            )
        })
    }
        return (
            <React.Fragment>
                <Navigationbar/>
                <div class="container">
                <div style={{float: "left"}}>
                    <center>
                    <h1 style={{margin: "10px", color:"red"}}> Your orders </h1>
                    </center>
                    <hr />
                    {renderOrders}
                    {renderFilterOrders}
                </div>
                    <div style={{float: "right"}}>
                       <h4 style={{margin: "10px", color:"red"}}> Filter </h4> 
                       <hr />
                       <Form onSubmit={this.handleFilter}>
                       <Form.Check
                            id = 'Order received'
                            label='Order received'
                            onChange={this.handleFilter}
                            style={{marginLeft:"10px" }}
                        />
                        <Form.Check
                            id = 'Preparing'
                            label='Preparing'
                            onChange={this.handleFilter}
                            style={{marginLeft:"10px" }}
                        />
                        <Form.Check
                            id = 'Pickup ready'
                            label='Pickup ready'
                            onChange={this.handleFilter}
                            style={{marginLeft:"10px" }}
                        />
                        <Form.Check
                            id = 'On the way'
                            label='On the way'
                            onChange={this.handleFilter}
                            style={{marginLeft:"10px" }}
                        />
                        <Form.Check
                            id = 'Picked up'
                            label='Picked up'
                            onChange={this.handleFilter}
                            style={{marginLeft:"10px" }}
                        />
                        <Form.Check
                            id = 'Delivered'
                            label='Delivered'
                            onChange={this.handleFilter}
                            style={{marginLeft:"10px" }}
                        />
                        <Button style={{marginLeft:"10px", marginTop: "10px", backgroundColor: "red", border: "1px solid red" }} type="submit"> Apply filter </Button>
                        </Form>
                    </div>

                </div>

            </React.Fragment>
        )
    }

}
export default userOrders;
