import { GET_USER, UPDATE_USER } from "./types";
import axios from "axios";

export const getUser = () => dispatch => {
    axios.get(`http://localhost:3001/yelp/userProfile/${localStorage.getItem("user_id")}`)
        .then(response => dispatch({
            type: GET_USER,
            payload: response.data
        }))
        .catch(error => {
            console.log(error);
        });
}

export const updateUser = (userProfileData) => dispatch => {
    axios.defaults.withCredentials = true;
    axios.post(`http://localhost:3001/yelp/userProfile/update`, userProfileData)
        .then(response => response.data)
        .then(data => {
            if (data === 'CUSTOMER_UPDATED') {
                localStorage.setItem("first_name", userProfileData.first_name);
                localStorage.setItem("last_name",  userProfileData.last_name )
                alert("Profile Updated Successfully!");
            }
            return dispatch({
                type: UPDATE_USER,
                payload: data
            })
        })
        .catch(error => {
            console.log(error);
        });
}
