import React, { Component } from 'react';
import { Map, GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react';
import Geocode from "react-geocode";

const mapStyles = {
    width: '100%',
    height: '70%'
  };

Geocode.setApiKey("AIzaSyDbhKxcXXJaBcKSg6-Hjvb6BeaHQjSuZYc");

export class MapContainer extends Component {
  constructor(props) {
    super(props);
    //this.state = {};
    this.initialise();
  } 

  state = {
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {},
    data: []
  };

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

    onClose = props => {
      if (this.state.showingInfoWindow) {
        this.setState({
          showingInfoWindow: false,
          activeMarker: null
        });
      }
    };

  initialise = () => {
    for (let restaurant of this.props.restaurantlist) {
      let location = restaurant.street.concat(' ', restaurant.city)
      console.log(location)
      let name = restaurant.name;
      Geocode.fromAddress(location)
    .then(response => {
            console.log("Hi")
            console.log(response.results[0].geometry.location);
            const { lat, lng } = response.results[0].geometry.location;
            this.state.data.push( <Marker position={{lat: lat, lng: lng}} 
              onClick={this.onMarkerClick}
              name={name}  
            />)
            this.forceUpdate()
          },
          error => {
            console.error(error);
          }
        )
    }
  }
  render() {
    return (
      <Map
        google={this.props.google}
        zoom={5}
        style={mapStyles}
        initialCenter={
        {
            lat: 37.336781,
            lng: -121.876350
          }
        }
      >
      <Marker
          position={{lat: 37.336781, lng: -121.876350}}
          name={'House of Bagels'}
        />

        <Marker
          position={{lat: 37.704491, lng: -121.866089}}
          name={'The Banana Garden'}
        />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow>
        </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyDbhKxcXXJaBcKSg6-Hjvb6BeaHQjSuZYc'
})(MapContainer);
